function randEmoji() {
    return emojis[Math.floor(Math.random() * emojis.length)];
}

export function createLogTracer(): void {
    const requestInfo = { traceId: DEBUG ? randEmoji() : v4() };
    store.set(AsyncHooks.executionAsyncId(), requestInfo);
}

function getRequestContext(): { traceId: string } {
    return store.get(AsyncHooks.executionAsyncId());
}

// TODO: Change pretty-print with pretty-tiny or custom:
// search can work for our filtering case but pretty-tiny has a better JS option
// traceId should always be an uuid, so we need to change the uuid to an emoji latter
// but with the default logger can be done but its not trivial
const prettyPrintConfig: Pino.PrettyOptions = {
    translateTime: 'd/m/yy HH:MM:ss.l',
    ignore: 'hostname,pid',
    // messageFormat: '[{traceId}] - {msg}',
    // search: '(contains(name, `core:`) && level >= `50`) || contains(name, `exposures:`)'
};

function createNamespacedLogger(namespace: string): Pino.Logger {
    const logger = Pino({
        name: namespace,
        nestedKey: 'payload',
        prettyPrint: DEBUG ? prettyPrintConfig : false,
        level: 'debug',
        mixin: () => {
            const context = getRequestContext();
            if (context) {
                return { traceId: context.traceId };
            } else {
                return {};
            }
        }
    });

    return logger;
}

export default createNamespacedLogger;